// let {natP = [1,2,3,4,5,6,8,9,10,12]; natN = map negate natP; nat = natP++natN;} in sequence_ $ fmap putStr $ map (\q -> "{" ++ show (numerator q) ++ ", " ++ show (denominator q) ++ "}, ") $ nub [m%n | m <- 0:nat, n <- nat]
Q sample[] = {
	{0, 1}, {1, 1}, {1, 2}, {1, 3}, {1, 4}, {1, 5}, {1, 6}, {1, 8}, {1, 9}, {1, 10}, {1, 12}, {-1, 1}, {-1, 2}, {-1, 3}, {-1, 4}, {-1, 5}, {-1, 6}, {-1, 8}, {-1, 9}, {-1, 10}, {-1, 12}, {2, 1}, {2, 3}, {2, 5}, {2, 9}, {-2, 1}, {-2, 3}, {-2, 5}, {-2, 9}, {3, 1}, {3, 2}, {3, 4}, {3, 5}, {3, 8}, {3, 10}, {-3, 1}, {-3, 2}, {-3, 4}, {-3, 5}, {-3, 8}, {-3, 10}, {4, 1}, {4, 3}, {4, 5}, {4, 9}, {-4, 1}, {-4, 3}, {-4, 5}, {-4, 9}, {5, 1}, {5, 2}, {5, 3}, {5, 4}, {5, 6}, {5, 8}, {5, 9}, {5, 12}, {-5, 1}, {-5, 2}, {-5, 3}, {-5, 4}, {-5, 6}, {-5, 8}, {-5, 9}, {-5, 12}, {6, 1}, {6, 5}, {-6, 1}, {-6, 5}, {8, 1}, {8, 3}, {8, 5}, {8, 9}, {-8, 1}, {-8, 3}, {-8, 5}, {-8, 9}, {9, 1}, {9, 2}, {9, 4}, {9, 5}, {9, 8}, {9, 10}, {-9, 1}, {-9, 2}, {-9, 4}, {-9, 5}, {-9, 8}, {-9, 10}, {10, 1}, {10, 3}, {10, 9}, {-10, 1}, {-10, 3}, {-10, 9}, {12, 1}, {12, 5}, {-12, 1}, {-12, 5}
};
int N = sizeof sample / sizeof(Q);

void sum1(void)
{
	printf("Sum of 1-powers\n");
	for (int i1 = 0; i1 < N; i1++) {
		bool flag = true;
		for (int n = 0; n < 7 && flag; n++) {
			int sum = 0;
			for (int i = 1; i <= n; i++) {
				sum += i;
			}
			float q2 = 1.0 / 2.0;
			float q1 = (float)sample[i1].a / (float)sample[i1].b;
			float q0 = 0.0;
			float val = q2*n*n + q1*n + q0;
			float d = val - (float)sum;
			d = d >= 0 ? d : -d;
			flag = flag && d < 0.01;						
			//if (flag) printf("val: %f = %d [d=%f]\n", val, sum, d);
		}
		if (flag) {
			printf("q2 = %d/%d\n", 1, 2);
			printf("q1 = %d/%d\n", sample[i1].a, sample[i1].b);
			printf("q0 = %d\n", 0);
			printf("----------\n");
		}
	}
}

void sum2(void)
{
	printf("Sum of 2-powers\n");
	for (int i2 = 0; i2 < N; i2++) {
		for (int i1 = 0; i1 < N; i1++) {
			bool flag = true;
			for (int n = 0; n < 7 && flag; n++) {
				int sum = 0;
				for (int i = 1; i <= n; i++) {
					sum += i * i;
				}
				float q3 = 1.0 / 3.0;
				float q2 = (float)sample[i2].a / (float)sample[i2].b;
				float q1 = (float)sample[i1].a / (float)sample[i1].b;
				float q0 = 0.0;
				float val = q3*n*n*n + q2*n*n + q1*n + q0;
				float d = val - (float)sum;
				d = d >= 0 ? d : -d;
				flag = flag && d < 0.01;						
				//if (flag) printf("val: %f = %d [d=%f]\n", val, sum, d);
			}
			if (flag) {
				printf("q3 = %d/%d\n", 1, 3);
				printf("q2 = %d/%d\n", sample[i2].a, sample[i2].b);
				printf("q1 = %d/%d\n", sample[i1].a, sample[i1].b);
				printf("q0 = %d\n", 0);
				printf("----------\n");
			}
		}
	}
}

void sum3(void)
{
	printf("Sum of 3-powers\n");
	for (int i3 = 0; i3 < N; i3++) {
		for (int i2 = 0; i2 < N; i2++) {
			for (int i1 = 0; i1 < N; i1++) {
				bool flag = true;
				for (int n = 0; n < 7 && flag; n++) {
					int sum = 0;
					for (int i = 1; i <= n; i++) {
						sum += i * i * i;
					}
					float q4 = 1.0 / 4.0;
					float q3 = (float)sample[i3].a / (float)sample[i3].b;
					float q2 = (float)sample[i2].a / (float)sample[i2].b;
					float q1 = (float)sample[i1].a / (float)sample[i1].b;
					float q0 = 0.0;
					float val = q4*n*n*n*n + q3*n*n*n + q2*n*n + q1*n + q0;
					float d = val - (float)sum;
					d = d >= 0 ? d : -d;
					flag = flag && d < 0.01;
				}
				if (flag) {
					printf("q4 = %d/%d\n", 1, 4);
					printf("q3 = %d/%d\n", sample[i3].a, sample[i3].b);
					printf("q2 = %d/%d\n", sample[i2].a, sample[i2].b);
					printf("q1 = %d/%d\n", sample[i1].a, sample[i1].b);
					printf("q0 = %d\n", 0);
					printf("----------\n");
				}
			}
		}
	}
}

void sum4(void)
{
	printf("Sum of 4-powers\n");
	for (int i4 = 0; i4 < N; i4++) {
		float progress = (float)i4 / (float)N;
		printf("Progress: %f%%\n", 100.0*progress);
		for (int i4 = 0; i4 < N; i4++) {
			for (int i3 = 0; i3 < N; i3++) {
				for (int i2 = 0; i2 < N; i2++) {
					for (int i1 = 0; i1 < N; i1++) {
						bool flag = true;
						for (int n = 0; n < 7 && flag; n++) {
							int sum = 0;
							for (int i = 1; i <= n; i++) {
								sum += i * i * i * i;
							}
							float q5 = 1.0 / 5.0;
							float q4 = (float)sample[i4].a / (float)sample[i4].b;
							float q3 = (float)sample[i3].a / (float)sample[i3].b;
							float q2 = (float)sample[i2].a / (float)sample[i2].b;
							float q1 = (float)sample[i1].a / (float)sample[i1].b;
							float q0 = 0.0;
							float val = q5*n*n*n*n*n + q4*n*n*n*n + q3*n*n*n + q2*n*n + q1*n + q0;
							float d = val - (float)sum;
							d = d >= 0 ? d : -d;
							flag = flag && d < 0.01;
						}
						if (flag) {
							printf("q5 = %d/%d\n", 1, 5);
							printf("q4 = %d/%d\n", sample[i4].a, sample[i4].b);
							printf("q3 = %d/%d\n", sample[i3].a, sample[i3].b);
							printf("q2 = %d/%d\n", sample[i2].a, sample[i2].b);
							printf("q1 = %d/%d\n", sample[i1].a, sample[i1].b);
							printf("q0 = %d\n", 0);
							printf("----------\n");
						}
					}
				}
			}
		}
	}
}


int main(void)
{
	printf("Size of sample rationals: %d\n", N);
	sum1();
	sum2();
	sum3();
	sum4();
	return 0;
}

